create table wksp_ebs2cloud_migration.xxcicd_debug_err_messages (
    err_message_id        number,
    err_message_timestamp timestamp(6) default systimestamp not null enable,
    error_message_level   varchar2(30 byte) not null enable,
    err_message_text      clob not null enable,
    err_count             number default 1,
    session_id            varchar2(100 byte),
    username              varchar2(100 byte),
    jira_number           varchar2(50 byte),
    jira_status           varchar2(50 byte),
    app_id                number,
    page_id               number,
    process_block_name    varchar2(100 byte),
    creation_date         date default sysdate not null enable
);

alter table wksp_ebs2cloud_migration.xxcicd_debug_err_messages add primary key ( err_message_id )
    using index enable;


